import React from 'react'
import styled, { createGlobalStyle } from 'styled-components'
import Announcement from '../composants/Announcement'
import { useSelector } from "react-redux";
import Footer from '../composants/Footer'
import Navbar from '../composants/Navbar'
import { Add, FavoriteRounded, Remove, ShoppingCartRounded } from '@material-ui/icons'
import { useEffect, useState } from "react";
import StripeCheckout from "react-stripe-checkout";
import { Link } from "react-router-dom";
import mobile from '../responsive';

const CONST_KEY = process.env.KEY_PUBLIC;

const Container = styled.div`

`
const Wrapper = styled.div`
   padding: 20px;   
   ${mobile({ padding: "10px" })}
`
const Title = styled.h1`
   font-weight: 600;
   text-align: center;
`
const Top = styled.div`
   display: flex;
   align-items: center;
   justify-content: space-between;
   padding: 10px;   

`
const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;border-radius: 20px;
  border: ${(props) => props.type === "check" && "none"};
  background-color: ${(props) =>props.type === "check" ? "black" : "transparent"};
  color: ${(props) => props.type === "check" && "white"};
  border-radius: 20px;
  &:hover{
    background-color: ${(props) =>props.type === "check" ? "#282828" : "#E0E0E0"};
    }     
`
const TopTexts = styled.div`
     ${mobile({ display: "none" })}
`
const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
  font-weight: 700;
`
const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
  background-color: #F8F8F8	;
  ${mobile({ flexDirection: "column" })}
`
const Info = styled.div`
  flex: 3;
`
const Product = styled.div`
  display: flex;
  justify-content: space-between;  
  border: 3px solid white; 
  ${mobile({ flexDirection: "column" })}
`;

const ProductDetail = styled.div`
  flex: 2;
  display: flex;
`;

const Image = styled.img`
  width: 200px;
`;

const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;

const ProductName = styled.span``;

const ProductId = styled.span``;

const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
`;

const ProductSize = styled.span``;

const PriceDetail = styled.div`
  flex: 2;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;  
  margin-left: 110 px;
`;

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;  
  ${mobile({ marginTop:"20px" })}
`;

const ProductAmount = styled.div`
  font-size: 24px;
  margin: 5px;
`

const ProductPrice = styled.div`
  font-size: 30px;
  font-weight: 200;
  margin-left: 222px;
`

const Espace = styled.hr`
  background-color: white;
  border: none;
  height: 3px;
`
const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 60vh;
  background-color: white;
`
const SummaryTitle = styled.h1`
  font-weight: 200;
`;

const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === "total" && "500"};
  font-size: ${(props) => props.type === "total" && "24px"};
`;

const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: #101010;
  color: white;
  font-weight: 600;
  cursor: pointer;
  border-radius: 20px;
  border: none;
  &:hover{
    background-color: #282828;
    }
`;


export default function Panier() {
  const panier = useSelector((state) => state.panier);
  const [stripeToken, setStripeToken] = useState(null);
 /*
  const setQuantite = (quantite) => {
  quantite=quantite - 1;  } 

  const MODQuantite = (type, quantite) => {
    if (type === "remove") {quantite > 1; quantite = quantite - 1;} 
    else {quantite += 1; }

  };
  onClick={() => {produit.quantite=produit.quantite+1}}
  */
  const Token = (token) => {
    setStripeToken(token);
  };
console.log(stripeToken)

  return (
    <Container>
      <Navbar/>
      <Announcement/>
      <Wrapper>
         <Top>
             <Link to={`/produits`}>
             <TopButton> Continuer vos achats </TopButton>
             </Link>
             <TopTexts>
                <FavoriteRounded style={{marginLeft: "20px"}}/>
             </TopTexts>
             <TopButton type="check"> Vider Votre Panier</TopButton>
         </Top>
         <Bottom>
         <Info>               
         { panier.produits.map((produit) => (      
           <Product>
              <ProductDetail>
                <Image src={produit.img} />
                <Details>
                  <ProductName>
                    <b>Product:</b> {produit.title}
                  </ProductName>
                  <ProductId>
                    <b>ID:</b> {produit._id}
                  </ProductId>
                  <ProductColor color={produit.color} />
                  <ProductSize>
                    <b>Size:</b> {produit.size}
                  </ProductSize>
                </Details>
              </ProductDetail>
              <PriceDetail>
                <ProductAmountContainer>
                <Remove style={{cursor: "pointer"}} />
                <ProductAmount>{produit.quantite}</ProductAmount>
                <Add style={{cursor: "pointer"}}  />
                </ProductAmountContainer>
                <ProductPrice>{produit.price*produit.quantite}.00 €</ProductPrice>
              </PriceDetail>              
            </Product> ))}
          </Info>
                    
          <Summary>
            <SummaryTitle>RÉCAPITULATIF DE LA COMMANDE</SummaryTitle>
            <SummaryItem>
              <SummaryItemText>Total sans livraison</SummaryItemText>
              <SummaryItemPrice>{panier.total}.00 €</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem>
              <SummaryItemText>Frais de livraison</SummaryItemText>
              <SummaryItemPrice>{panier.total>100 || panier.total==0  ? 0 : 15}.00 €</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem>
              <SummaryItemText></SummaryItemText>
              <SummaryItemPrice></SummaryItemPrice>
            </SummaryItem>
            <SummaryItem type="total">
              <SummaryItemText>Total</SummaryItemText>
              <SummaryItemPrice>{panier.total>100 || panier.total==0  ? panier.total : panier.total+15}.00 €</SummaryItemPrice>
            </SummaryItem>
            <StripeCheckout
              name="sara saad"          
              billingAddress
              shippingAddress
              description={`Vous devez payer ${panier.total}.00 €`}
              amount={panier.total * 100}
              token={Token}
              stripeKey={CONST_KEY}
            >
              <Button>Payer maintenant</Button>
            </StripeCheckout>
          </Summary>
         </Bottom>
      </Wrapper>
      <Footer/>
    </Container>
  )
}
