import React from 'react'
import styled from 'styled-components'
import { useDispatch, useSelector } from "react-redux"
import { login,logout } from "../redux/FontionAPI"
import { useState } from "react"
import { useHistory } from 'react-router-dom'
import mobile from '../responsive'
import {Home} from "@material-ui/icons"

const Container = styled.div`
    width: 100vw;
    height: 100vh;
    background-size: cover;
    background-color: #F0F0F0;
    display: flex;
    align-items: center;
    justify-content: center;    
`
const Wrapper = styled.div`
  width: 40%;
  height: 53%;
  padding: 40px;
  background-color: white;
  border-radius: 10px;
  ${mobile({ width: "70%"})}
`

const Title = styled.h1`
  font-size: 24px;
  font-weight: 700;
`

const Form = styled.form`
  display: flex;
  flex-direction: column;
`


const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 10px 0px;
  padding: 10px;
  margin-top: 15px;
`
const Button = styled.button`
  width: 40%;
  border: none;
  margin-top: 20px;
  padding: 15px 20px;
  background-color: #101010	;
  color: white;
  cursor: pointer;
  font-weight: 600;  
  ${mobile({ width: "30%"})}
  border-radius: 10px 100px / 120px;
  &:hover{
    background-color: #696969;
    }
`
const Error = styled.span`
  color: #B22222;
  font-weight: 700;
  text-align: center;
`;

const Link = styled.a`
  margin: 5px 0px;
  font-size: 12px;
  text-decoration: underline;
  cursor: pointer;
  font-weight: 500;
  ${mobile({ fontSize: "10px"})}
`

export default function Login() {
  const d = useDispatch();
  const [email,setEmail]= useState("");
  const [password,setPassword]= useState("");
  const {chercher, error } = useSelector((state) => state.utilisateur);
  const Click = (e) => {
    e.preventDefault();
    login(d, { email, password });
  }; 
    let url = "/register";
  let history = useHistory();
  const P = useDispatch();
  const lien = (e) => {
    history.push(url);
  };

return (
    <Container>
     <Home/>
    <Wrapper>
      <Title>CONNEXION</Title>
      <Form>
        {error && <Error>Veuillez verifier votre mail et password !!</Error>}
        <Input placeholder="email" onChange={(e) => setEmail(e.target.value) }/>
        <Input placeholder="password" onChange={(e) => setPassword(e.target.value) } type="password"/>
        <Button onClick={Click} disabled={chercher}>Se Connecter</Button>        
        <Link style={{ marginTop : "20px" }}>DO NOT YOU REMEMBER THE PASSWORD ?</Link>
        <Link to="/register" onClick={lien}>CREATE A NEW ACCOUNT</Link>
      </Form>
    </Wrapper>
  </Container>
  )
}
