import React from 'react'
import styled from 'styled-components'
import {ProductPopulaire} from "../data"
import ProductsItem from './ProductsItem'
import { useState , useEffect} from "react";
import axios from "axios";

const Container = styled.div`
padding: 20px;
display: flex;
flex-wrap: wrap;
justify-content: space-between;
`

export default function Products({ categorie, filtre, sort }) {
    const [produits, setProduits] = useState([]);
    const [produitsFiltrer, setproduitsFiltrer] = useState([]);
   // console.log({ categorie, filtre, sort })
    useEffect(() => {
      const getProduits = async () => {
        try {
          const res = await axios.get( categorie ? `http://localhost:8080/api/products?category=${categorie}`:`http://localhost:8080/api/products`);
        } catch (err) {}
        setproduitsFiltrer(res.data);
      };
      getProduits()
    }, [categorie]);

    useEffect(() => {
      categorie &&  setproduitsFiltrer(
        produits.filter((item) =>
            Object.entries(filtre).every(([key, value]) =>
              item[key].includes(value)
            )
          )
        );
    }, [produits, categorie, filtre]);


  return (
    <Container>
        {produitsFiltrer.map(items =>
            <ProductsItem items={items} key={items.id}/>
        )}
    </Container>
  )
}
