import { createSlice } from "@reduxjs/toolkit";

const wishlist = createSlice({
  name: "wishlist",
  initialState: {
    produits: [],
    quantit: 0,
  },
  reducers: {
    ajouterproduitFavori : (state, action) => {
      state.quantit += 1;
      state.produits.push(action.payload);
    },
    supprimerproduitFavori : (state, action) => {
     if (state.quantit>0) {state.quantit -= 1;
      state.produits=[];
      state.produits.push(action.payload);}
    },
  },
});

export const { ajouterproduitFavori  } = wishlist.actions;
export const { supprimerproduitFavori  } = wishlist.actions;
export default wishlist.reducer;
